import { LoginButton, LoginCard, LoginContainer, LoginInput, LoginTitle } from "./styles";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import client from "../../services/client";
import { useState } from "react";
import { redirect } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [token, setToken] = useState<string>("");

  const login = async () => {
    const { data, status } = await client.get("/status", {
      headers: {
        "x-rapidapi-key": token,
        "x-rapidapi-host": "v3.football.api-sports.io",
      },
    });

    if (status !== 200) {
      return toast.error("Ops! Ocorreu um erro. Tente novamente mais tarde");
    }

    if (data.results < 1 || data.errors.length > 0) {
      return toast.error("Ops! Api key inválida.");
    }

    localStorage.setItem("user_token", JSON.stringify({ token }));
    return true;
  };

  return (
    <>
      <ToastContainer />
      <LoginContainer>
        <div>
          <LoginTitle>Football Stats</LoginTitle>
          <LoginCard>
            <LoginInput
              placeholder="Api Key"
              onChange={({ target }) => setToken(target.value)}
            ></LoginInput>
            <LoginButton onClick={() => login()}>Login</LoginButton>
          </LoginCard>
        </div>
      </LoginContainer>
      {login() && redirect("/home")}
    </>
  );
};

export default Login;
