import { useEffect, useState } from "react";
import client from "../../services/client";
import { HomeContainer, HomeStyle, StyledSelect } from "./styles";
import { ToastContainer, toast } from "react-toastify";
import RequestHeader from "../../utils";

function Home() {
  const [countries, setCountries] = useState(null);

  const fetchCountries = async () => {
    try {
      const { data, status } = await client.get("/countries");
      console.log(data);
    } catch (e) {
      console.error(e);
      toast.error("Ops. Ocorreu um erro ao buscar os países. Tente novamente");
    }
  };

  useEffect(() => {
    fetchCountries();
  }, []);

  return (
    <>
      <ToastContainer />
      <HomeStyle>
        <HomeContainer>
          <div>
            <label>
              Selecione um país
              <StyledSelect>
                <option>Pais A</option>
              </StyledSelect>
            </label>
          </div>
          <div>
            <label>
              Selecione uma temporada
              <StyledSelect>
                <option>Pais A</option>
              </StyledSelect>
            </label>
          </div>
          <div>
            <label>
              Selecione uma liga
              <StyledSelect>
                <option>Pais A</option>
              </StyledSelect>
            </label>
          </div>
          <div>
            <label>
              Selecione um time
              <StyledSelect>
                <option>Pais A</option>
              </StyledSelect>
            </label>
          </div>
        </HomeContainer>
      </HomeStyle>
    </>
  );
}

export default Home;
