import { useEffect, useState } from "react";
import client from "../../services/client";
import { HomeContainer, HomeStyle, StyledSelect } from "./styles";
import { ToastContainer, toast } from "react-toastify";
import { getHeadersRequest } from "../../utils";
import { Country } from "../../interfaces/Home/home";
import { url } from "inspector";

function Home() {
  const [countries, setCountries] = useState<Country[]>([]);
  const [selectedCountry, setSelectedCountry] = useState<string>("");
  const headersRequest = getHeadersRequest();

  const fetchCountries = async () => {
    try {
      const { data, status } = await client.get("/countries", headersRequest);
      setCountries(data.response);
    } catch (e) {
      console.error(e);
      toast.error("Ops. Ocorreu um erro ao buscar os países. Tente novamente");
    }
  };

  useEffect(() => {
    fetchCountries();
  }, []);

  useEffect(() => {
    fetchCountries();
  }, []);

  console.log(selectedCountry);

  return (
    <>
      <ToastContainer />
      <HomeStyle>
        <HomeContainer>
          <div>
            <label>
              Selecione um país
              <StyledSelect onChange={({ target }) => setSelectedCountry(target.value)}>
                <option selected value="">
                  Selecione
                </option>
                {countries?.map((country: Country) => {
                  return <option value={country.code}>{country.name}</option>;
                })}
              </StyledSelect>
            </label>
          </div>
          <div>
            <label>
              Selecione uma temporada
              <StyledSelect disabled={!selectedCountry}>
                <option>Pais A</option>
              </StyledSelect>
            </label>
          </div>
          <div>
            <label>
              Selecione uma liga
              <StyledSelect>
                <option>Pais A</option>
              </StyledSelect>
            </label>
          </div>
          <div>
            <label>
              Selecione um time
              <StyledSelect>
                <option>Pais A</option>
              </StyledSelect>
            </label>
          </div>
        </HomeContainer>
      </HomeStyle>
    </>
  );
}

export default Home;
