import { LoginButton, LoginCard, LoginContainer, LoginInput, LoginTitle } from "./styles";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import client from "../../services/client";

const Login = () => {
  const login = async () => {
    const { data, status } = await client.get("/status");

    if (status !== 200) {
      return toast.error("Ops! Ocorreu um erro. Tente novamente mais tarde");
    }

    if (data.results < 1 || data.errors) {
      return toast.error("Ops! Api key inválida.");
    }

    // localStorage.setItem("user_token", JSON.stringify({ token }));
  };

  return (
    <>
      <ToastContainer />
      <LoginContainer>
        <div>
          <LoginTitle>Football Stats</LoginTitle>
          <LoginCard>
            <LoginInput placeholder="Api Key"></LoginInput>
            <LoginButton onClick={() => login()}>Login</LoginButton>
          </LoginCard>
        </div>
      </LoginContainer>
    </>
  );
};

export default Login;
