import styled from "styled-components";

export const LoginContainer = styled.div`
  background-image: linear-gradient(180deg, #bce5ff 0, #9db9f2 50%, #7e8fb3 100%);

  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;
